mapper.select:
SELECT * FROM PRODUCT WHERE DIV1=#{div1}

and p_name LIKE CONCAT('%',#{searchKeyword},'%')
or div1 LIKE CONCAT('%',#{searchKeyword},'%')
or div2 LIKE CONCAT('%',#{searchKeyword},'%')

ORDER BY NO DESC LIMIT 6 OFFSET #{offset}

SELECT * FROM PRODUCT
WHERE p_name LIKE CONCAT('%',#{searchKeyword},'%') or div1 LIKE CONCAT('%',#{searchKeyword},'%') or div2 LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY NO DESC LIMIT 6 OFFSET #{offset}

SELECT * FROM PRODUCT
WHERE p_name LIKE CONCAT('%',#{searchKeyword},'%') or no LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY NO DESC LIMIT 10 OFFSET #{offset}

mapper.update:

UPDATE PRODUCT
SET P_FILE=#{p_file},TEXT=#{text}
WHERE NO=#{no}
